	deletePrompt = () => {
		Alert.alert(
			"Are you sure you want to delete this bike?",
			"",
			[
				{ text: "Yes", onPress: () => {this._enableLoader(); this.AddBikeP.deleteBike(this.state.currentID, this.deleteCallback)}},
				{ text: "No", onPress: () => {}, style: "cancel" },
