import React, { Component } from 'react';
import { AsyncStorage } from 'react-native';

/**
 * Class for the persistent storage of data on a device using ASyncStorage.
 * @extends Component
 */
class PersistentStorage extends Component {
	/**
	 * Creates an instance of AuthLoadingView
	 *
	 * @constructor
	 * @param {Object} props - Component properties
	 */
	constructor(props) {
		super(props);
	}

	/**
	 * Stores key, data using AsyncStorage to the device.
	 *
	 * @param {string} key - A key to store data with
	 * @param {string/Number} value - A value to store (can be string or number)
	 * @param onError - An error callback to call if there's an error while storing data
	 */
	async storeData(key, value, onError) {
		if (this.checkValid(key) && this.checkValid(value)) {
			try {
				await AsyncStorage.setItem(key, value);
			} catch (error) {
				onError(error);
			}
		} else {
			onError('Key or Value is undefined or null');
		}
	}

	/**
	 * Checks if the parameter passed in is valid.
	 *
	 * @param {string} val - A value to check
	 * @return {Boolean} true: Value is valid; false: Value is invalid
	 */
	checkValid(val) {
		return val != undefined && val != null;
	}

	/**
	 * Retrieves data from AsyncStorage and calls the success callback with the value.
	 *
	 * @param {string} key - A key to fetch the data for
	 * @param {Function} onSuccess - A success callback to process the data
	 * @param {Function} onError - A failure callback to call if an error occurs
	 */
	async retrieveData(key, onSuccess, onError) {
		if (this.checkValid(key)) {
			try {
				const value = await AsyncStorage.getItem(key);
				onSuccess(value); // Let someone else deal with the logic of it being null or not
			} catch (error) {
				onError(error);
			}
		} else {
			onError('Key is undefined or null')
		}
	}

	/**
	 * Removes an item from the persistent storage.
	 *
	 * @param {string} key - The key for an item that should be removed
	 * @param {Function} onError - A failure callback if an error occurs
	 */
	async removeData(key, onError) {
		if (this.checkValid(key)) {
			try {
				await AsyncStorage.removeItem(key);
			} catch (error) {
				onError(error);
			}
		} else {
			onError('Key is undefined or null');
		}
	}

	/**
	 * Get all the stored keys.
	 *
	 * @param {Function} onComplete - A callback function to call with an array of keys and an error if it failed. onComplete(error, keys)
	 */
	async getAllStoredKeys(onComplete) {
		await AsyncStorage.getAllKeys(onComplete);
	}

	/**
	 * Removes all stored keys in the AsyncStorage.
	 *
	 * @param {List} keepKeys - A list of keys as strings to keep in local storage.
	 * @param {Function} onSuccess - A callback function to call if all keys were successfully removed.
	 * @param {Function} onError - A callback function to call if one or more keys threw an error when being removed.
	 */
	async removeAllData(keepKeys, onSuccess, onError) {
		this.getAllStoredKeys(async (error, keys) => {
			keys = keys.filter( ( el ) => !keepKeys.includes(el));
			await AsyncStorage.multiRemove(keys, (err) => {
				if (err == null) {
					const message = keepKeys.length === 0 ? '' : "- Except: " + (keepKeys.length === 1 ? keepKeys : keepKeys.join(', '));
					onSuccess(message);
				} else {
					console.log(err);
					onError(err);
				}
			});
		});
	}
}

const PersistStorage = new PersistentStorage();
export default PersistStorage;

/**
 * Utility class for time functions.
 */
class TimeUtility {
	/**
	 * Returns the milliseconds time formatted as 'HH:MM - DD/MM/YY'.
	 *
	 * @param {Number} datetime - The time in milliseconds
	 * @return {string} Milliseconds represented as a string
	 */
	getDateFormatFromDateTime(datetime) {
		let converted = new Date(datetime);
		let date = converted.getDate() + '/' + (converted.getMonth()+1) + '/' + converted.getFullYear();
		const hours = converted.getHours();
		const minutes = converted.getMinutes();
		let time = (hours > 9 ? hours : '0' + hours) + ":" + (minutes > 9 ? minutes : '0' + minutes);
		let dateTime = time + ' - ' + date;
		return dateTime
	}

	/**
	 * Returns millisecond time as a time ago string (# __ ago).
	 *
	 * @param {Number} milliseconds - The time in milliseconds
	 * @return {string} The millisecond time represented as a string
	 */
	getTimeAgoFromMilliseconds(milliseconds) {
		const currentTime = new Date();

		let time = this.parseMillisecondsIntoReadableTime(currentTime-milliseconds);

		let parsetime = time.split(':');
		let suffix = '';
		let outtime = '';

		const hours = parseInt(parsetime[0]);
		const minutes = parseInt(parsetime[1]);
		const seconds = parseInt(parsetime[2]);

		// console.log(hours, minutes, seconds);

		// Probably could make more concise
		if (hours >= 24) {
			outtime = Math.floor(hours/24); // Round to days
			suffix = 'd';

			if (outtime >= 30 && outtime < 365) { // 30 days - 365 days
				outtime = Math.floor(outtime/30); // Round to months
				suffix = 'mo';
			} else if (outtime >= 365) { // > 365 days
				outtime = Math.floor(outtime/365); // Round to years
				suffix = 'yr';
			}

		} else if (hours > 0) {
			outtime = hours;
			suffix = 'hr';

		} else {
			if (minutes > 0) {
				outtime = minutes;
				suffix = outtime === 1 ? ' min' : ' mins';
			} else {
				outtime = seconds;
				suffix = 's';
			}
		}

		return outtime + suffix;
	}

	/**
	 * Returns the milliseconds time as readable time (HH:MM:SS)
	 *
	 * @param {Number} milliseconds - The time in milliseconds
	 * @return {string} Milliseconds formatted to time
	 */
	parseMillisecondsIntoReadableTime(milliseconds){
		//  Get hours from milliseconds
		let hours = milliseconds / (1000*60*60);
		let absoluteHours = Math.floor(hours);
		let h = absoluteHours > 9 ? absoluteHours : '0' + absoluteHours;

		// Get remainder from hours and convert to minutes
		let minutes = (hours - absoluteHours) * 60;
		let absoluteMinutes = Math.floor(minutes);
		let m = absoluteMinutes > 9 ? absoluteMinutes : '0' +  absoluteMinutes;

		// Get remainder from minutes and convert to seconds
		let seconds = (minutes - absoluteMinutes) * 60;
		let absoluteSeconds = Math.floor(seconds);
		let s = absoluteSeconds > 9 ? absoluteSeconds : '0' + absoluteSeconds;

		return h + ':' + m + ':' + s;
	}

	/**
	 * Quick sorts the data based on the timeago property. Arguably faster than using the built in sort function
	 * with a custom compare function.
	 *
	 * @param {List} data - Unsorted object data with a timeago property
	 * @return {List} Sorted data
	 */
	sortOnTime(data) {
		if (data.length <= 1) {
			return data;
		}

		let pivot = data[0];

		let left = []; 
		let right = [];

		for (let i = 1; i < data.length; i++) {
			data[i].milliseconds < pivot.milliseconds ? left.push(data[i]) : right.push(data[i]);
		}

		return this.sortOnTime(left).concat(pivot, this.sortOnTime(right));
	};

	/**
	 * Returns the current date and time in milliseconds.
	 * 
	 * @return {Number} The date and time in milliseconds
	 */
	getDateTime() {
		return (new Date()).getTime();
	}
}

const TimeUtil = new TimeUtility();
export default TimeUtil;

